    &.active {
      background: map-get($color, primary);
      top: 100%;
      opacity: 1;
      z-index: 99;
      min-height: 50vh;
      transition: all 0.5 ease;
    }

    .navbar__item {
      width: 100%;

      .navbar__links {
        text-align: center;
        padding: 2rem;
        width: 100%;
      }
    }
  }

  .navbar__btn {
    display: none;
  }

  .navbar__toggle {
    display: flex;

    .burger {
      align-self: center;
      cursor: pointer;
      padding-left: 20px;
    }

    .bar {
      width: 24px;
      height: 3px;
      margin: 4px auto;
      border-radius: 50px;
      transition: all 0.2s ease-in-out;
      background: map-get($color, text);
      display: block;
    }
  }

  #mobile-menu .bar:nth-child(2) {
    width: 19px;
  }

  #mobile-menu.is-active .bar:nth-child(2) {
    opacity: 0;
  }

  #mobile-menu.is-active .bar:nth-child(1) {
    transform: translateY(7px) rotate(45deg);
  }

  #mobile-menu.is-active .bar:nth-child(3) {
    transform: translateY(-7px) rotate(-45deg);
  }
}

